import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Paper,
  IconButton,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import {
  CallEnd,
  Mic,
  MicOff,
  Videocam,
  VideocamOff,
  ScreenShare,
  AttachFile,
} from '@mui/icons-material';
import { styled } from '@mui/system';

const socket = io('http://localhost:5889'); // Connect to your backend server

const VideoContainer = styled(Box)({
  display: 'flex',
  justifyContent: 'center',
  gap: '10px',
  marginBottom: '20px',
});

const ChatContainer = styled(Paper)({
  padding: '10px',
  height: '500px',
  display: 'flex',
  flexDirection: 'column',
  borderRadius: '10px',
});

const MessageList = styled(List)({
  flex: 1,
  overflowY: 'auto',
  marginBottom: '10px',
});

const CallRoom = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [stream, setStream] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isNormalCall, setIsNormalCall] = useState(false); // State for normal call (audio-only)
  const [showCallTypeDialog, setShowCallTypeDialog] = useState(true); // State for pop-up dialog
  const userVideoRef = useRef();
  const peerVideoRef = useRef();
  const peerRef = useRef();
  const fileInputRef = useRef(); // Ref for file input

  // Memoize the createPeer function using useCallback
  const createPeer = useCallback(
    (initiator = true) => {
      const peer = new Peer({ initiator, stream });

      peer.on('signal', (data) => {
        socket.emit('signal', { roomId, data });
      });

      peer.on('stream', (stream) => {
        if (peerVideoRef.current) {
          peerVideoRef.current.srcObject = stream;
        }
      });

      peerRef.current = peer;
    },
    [stream, roomId] // Dependencies for useCallback
  );

  useEffect(() => {
    if (!showCallTypeDialog) {
      // Get user media with optimized video constraints
      const mediaConstraints = isNormalCall
        ? { audio: true, video: false } // Audio-only for normal call
        : {
            audio: true,
            video: { width: 640, height: 480, frameRate: 15 }, // Lower resolution and frame rate
          };

      navigator.mediaDevices.getUserMedia(mediaConstraints).then((stream) => {
        setStream(stream);
        if (userVideoRef.current) {
          userVideoRef.current.srcObject = stream;
        }
      });

      // Join the room
      socket.emit('join-room', roomId);

      // Handle signaling data
      socket.on('signal', (data) => {
        if (!peerRef.current) {
          createPeer(data);
        } else {
          peerRef.current.signal(data);
        }
      });

      // Handle receiving chat messages
      socket.on('receive-message', (data) => {
        if (data && (data.message || data.media)) {
          setMessages((prev) => [...prev, data]);
        } else {
          console.error('Invalid message received:', data);
        }
      });
    }

    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
      socket.disconnect();
    };
  }, [roomId, isNormalCall, showCallTypeDialog, createPeer, stream]); // Add missing dependencies

  const sendMessage = () => {
    if (newMessage) {
      const messageData = { roomId, message: newMessage, sender: socket.id };
      socket.emit('send-message', messageData);
      setMessages((prev) => [...prev, messageData]);
      setNewMessage('');
    }
  };

  const sendMedia = (file) => {
    const reader = new FileReader();
    reader.onload = () => {
      const mediaData = {
        roomId,
        media: reader.result, // Base64 URL of the media file
        sender: socket.id,
        type: file.type.startsWith('image') ? 'image' : 'video',
      };
      socket.emit('send-message', mediaData);
      setMessages((prev) => [...prev, mediaData]);
    };
    reader.readAsDataURL(file); // Convert file to Base64 URL
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.type.startsWith('image/') || file.type.startsWith('video/')) {
        sendMedia(file);
      } else {
        alert('Please upload an image or video file.');
      }
    }
  };

  const endCall = () => {
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
    }
    if (peerRef.current) {
      peerRef.current.destroy();
    }
    navigate('/'); // Redirect to home page after ending the call
  };

  const toggleMute = () => {
    if (stream) {
      const audioTrack = stream.getAudioTracks()[0];
      audioTrack.enabled = !audioTrack.enabled;
      setIsMuted(!audioTrack.enabled);
    }
  };

  const toggleVideo = () => {
    if (stream) {
      const videoTrack = stream.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled;
        setIsVideoOn(!videoTrack.enabled);
      }
    }
  };

  const handleCallTypeSelection = (isNormal) => {
    setIsNormalCall(isNormal);
    setShowCallTypeDialog(false); // Close the dialog
  };

  const shareScreen = () => {
    navigator.mediaDevices.getDisplayMedia({ video: true }).then((screenStream) => {
      if (peerRef.current) {
        peerRef.current.replaceTrack(
          stream.getVideoTracks()[0],
          screenStream.getVideoTracks()[0],
          stream
        );
        screenStream.getTracks()[0].onended = () => {
          peerRef.current.replaceTrack(
            screenStream.getVideoTracks()[0],
            stream.getVideoTracks()[0],
            stream
          );
        };
      }
    });
  };

  return (
    <Container>
      {/* Pop-up dialog for call type selection */}
      <Dialog open={showCallTypeDialog} onClose={() => setShowCallTypeDialog(false)}>
        <DialogTitle>Choose Call Type</DialogTitle>
        <DialogContent>
          <Typography>Do you want to start a normal call (audio-only) or a video call?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => handleCallTypeSelection(true)}>Normal Call</Button>
          <Button onClick={() => handleCallTypeSelection(false)}>Video Call</Button>
        </DialogActions>
      </Dialog>

      <Typography variant="h4" align="center" gutterBottom>
        Room: {roomId}
      </Typography>
      <VideoContainer>
        {!isNormalCall && (
          <video
            ref={userVideoRef}
            autoPlay
            muted
            style={{ width: '45%', borderRadius: '10px' }}
          />
        )}
        {!isNormalCall && (
          <video ref={peerVideoRef} autoPlay style={{ width: '45%', borderRadius: '10px' }} />
        )}
      </VideoContainer>
      <Box display="flex" justifyContent="center" gap={2} mb={4}>
        <IconButton color={isMuted ? 'secondary' : 'primary'} onClick={toggleMute}>
          {isMuted ? <MicOff /> : <Mic />}
        </IconButton>
        {!isNormalCall && (
          <IconButton color={isVideoOn ? 'primary' : 'secondary'} onClick={toggleVideo}>
            {isVideoOn ? <Videocam /> : <VideocamOff />}
          </IconButton>
        )}
        {!isNormalCall && (
          <IconButton color="primary" onClick={shareScreen}>
            <ScreenShare />
          </IconButton>
        )}
        <IconButton color="secondary" onClick={endCall}>
          <CallEnd />
        </IconButton>
      </Box>
      <Box display="flex" justifyContent="space-between" mt={3}>
        <Box flex={1} mr={2}>
          <ChatContainer elevation={3}>
            <Typography variant="h6" gutterBottom>
              Chat
            </Typography>
            <MessageList>
              {messages.map((msg, index) => (
                <ListItem key={index}>
                  <ListItemAvatar>
                    <Avatar>{msg.sender ? msg.sender[0] : 'U'}</Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary={msg.sender || 'Unknown'}
                    secondary={
                      msg.media ? (
                        msg.type === 'image' ? (
                          <img
                            src={msg.media}
                            alt="Shared content"
                            style={{ maxWidth: '100%', borderRadius: '5px' }}
                          />
                        ) : (
                          <video
                            src={msg.media}
                            controls
                            style={{ maxWidth: '100%', borderRadius: '5px' }}
                          />
                        )
                      ) : (
                        msg.message
                      )
                    }
                  />
                </ListItem>
              ))}
            </MessageList>
            <Box display="flex">
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Type a message"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              />
              <IconButton color="primary" onClick={() => fileInputRef.current.click()}>
                <AttachFile />
              </IconButton>
              <Button variant="contained" color="primary" onClick={sendMessage} style={{ marginLeft: '10px' }}>
                Send
              </Button>
            </Box>
            <input
              type="file"
              ref={fileInputRef}
              style={{ display: 'none' }}
              accept="image/*, video/*"
              onChange={handleFileUpload}
            />
          </ChatContainer>
        </Box>
      </Box>
    </Container>
  );
};

export default CallRoom;
