import { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import { Container, Typography, TextField, Button, Box, Paper, IconButton } from '@mui/material';
import { CallEnd } from '@mui/icons-material';

const socket = io('http://localhost:5889'); // Connect to your backend server

const CallRoom = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [stream, setStream] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const userVideoRef = useRef();
  const peerVideoRef = useRef();
  const peerRef = useRef();

  useEffect(() => {
    // Get user media (video and audio)
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setStream(stream);
      if (userVideoRef.current) {
        userVideoRef.current.srcObject = stream;
      }
    });

    // Join the room
    socket.emit('join-room', roomId);

    // Handle signaling data
    socket.on('signal', (data) => {
      if (!peerRef.current) {
        createPeer(data);
      } else {
        peerRef.current.signal(data);
      }
    });

    // Handle receiving chat messages
    socket.on('receive-message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
      socket.disconnect();
    };
  }, [roomId]);

  const createPeer = (initiator = true) => {
    const peer = new Peer({ initiator, stream });

    peer.on('signal', (data) => {
      socket.emit('signal', { roomId, data });
    });

    peer.on('stream', (stream) => {
      if (peerVideoRef.current) {
        peerVideoRef.current.srcObject = stream;
      }
    });

    peerRef.current = peer;
  };

  const sendMessage = () => {
    if (newMessage) {
      const messageData = { roomId, message: newMessage, sender: socket.id };
      socket.emit('send-message', messageData);
      setMessages((prev) => [...prev, messageData]);
      setNewMessage('');
    }
  };

  const endCall = () => {
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
    }
    if (peerRef.current) {
      peerRef.current.destroy();
    }
    navigate('/'); // Redirect to home page after ending the call
  };

  return (
    <Container>
      <Typography variant="h4" align="center" gutterBottom>
        Room: {roomId}
      </Typography>
      <Box display="flex" justifyContent="space-between" mt={3}>
        <Box flex={1} mr={2}>
          <Paper elevation={3} style={{ padding: '10px' }}>
            <Typography variant="h6" gutterBottom>
              Video Call
            </Typography>
            <Box display="flex" justifyContent="center">
              <video ref={userVideoRef} autoPlay muted style={{ width: '100%', maxWidth: '400px', borderRadius: '10px' }} />
              <video ref={peerVideoRef} autoPlay style={{ width: '100%', maxWidth: '400px', borderRadius: '10px' }} />
            </Box>
            <Box display="flex" justifyContent="center" mt={2}>
              <IconButton color="secondary" onClick={endCall}>
                <CallEnd fontSize="large" />
              </IconButton>
            </Box>
          </Paper>
        </Box>
        <Box flex={1} ml={2}>
          <Paper elevation={3} style={{ padding: '10px', height: '500px', display: 'flex', flexDirection: 'column' }}>
            <Typography variant="h6" gutterBottom>
              Chat
            </Typography>
            <Box flex={1} overflow="auto" mb={2}>
              {messages.map((msg, index) => (
                <Box key={index} mb={1}>
                  <Typography variant="body1">
                    <strong>{msg.sender}:</strong> {msg.message}
                  </Typography>
                </Box>
              ))}
            </Box>
            <Box display="flex">
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Type a message"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              />
              <Button variant="contained" color="primary" onClick={sendMessage} style={{ marginLeft: '10px' }}>
                Send
              </Button>
            </Box>
          </Paper>
        </Box>
      </Box>
    </Container>
  );
};

export default CallRoom;
