import React, { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Paper,
  IconButton,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
} from '@mui/material';
import {
  CallEnd,
  Mic,
  MicOff,
  Videocam,
  VideocamOff,
  ScreenShare,
  BlurOn,
  InvertColors,
  FilterNone,
} from '@mui/icons-material';
import { styled } from '@mui/system';
import * as tf from '@tensorflow/tfjs';
import * as bodyPix from '@tensorflow-models/body-pix';

const socket = io('http://localhost:5889'); // Connect to your backend server

const VideoContainer = styled(Box)({
  display: 'flex',
  justifyContent: 'center',
  gap: '10px',
  marginBottom: '20px',
});

const ChatContainer = styled(Paper)({
  padding: '10px',
  height: '500px',
  display: 'flex',
  flexDirection: 'column',
  borderRadius: '10px',
});

const MessageList = styled(List)({
  flex: 1,
  overflowY: 'auto',
  marginBottom: '10px',
});

const CallRoom = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [stream, setStream] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isBlurEnabled, setIsBlurEnabled] = useState(false); // State for background blur
  const [theme, setTheme] = useState('none'); // State for themes
  const userVideoRef = useRef();
  const peerVideoRef = useRef();
  const peerRef = useRef();
  const canvasRef = useRef(); // Canvas for background blur

  useEffect(() => {
    // Get user media (video and audio)
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setStream(stream);
      if (userVideoRef.current) {
        userVideoRef.current.srcObject = stream;
      }
    });

    // Join the room
    socket.emit('join-room', roomId);

    // Handle signaling data
    socket.on('signal', (data) => {
      if (!peerRef.current) {
        createPeer(data);
      } else {
        peerRef.current.signal(data);
      }
    });

    // Handle receiving chat messages
    socket.on('receive-message', (data) => {
      if (data && data.sender && data.message) {
        setMessages((prev) => [...prev, data]);
      } else {
        console.error('Invalid message received:', data);
      }
    });

    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
      socket.disconnect();
    };
  }, [roomId]);

  // Load BodyPix model and apply background blur
  useEffect(() => {
    if (isBlurEnabled) {
      const loadBodyPix = async () => {
        const net = await bodyPix.load();
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        const processFrame = async () => {
          if (userVideoRef.current && isBlurEnabled) {
            const video = userVideoRef.current;

            // Ensure video has valid dimensions
            if (video.videoWidth === 0 || video.videoHeight === 0) {
              requestAnimationFrame(processFrame);
              return;
            }

            // Resize canvas to match video dimensions
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;

            // Segment the person from the background
            const segmentation = await net.segmentPerson(video);
            const backgroundBlurAmount = 10; // Adjust blur intensity

            // Draw the blurred background
            bodyPix.drawBokehEffect(
              canvas,
              video,
              segmentation,
              backgroundBlurAmount,
              3 // Use a numeric value for blur strength (e.g., 3 for LOW)
            );

            requestAnimationFrame(processFrame);
          }
        };

        // Wait for the video to load before processing frames
        setTimeout(() => {
          processFrame();
        }, 500); // Adjust delay if needed
      };

      loadBodyPix();
    }
  }, [isBlurEnabled]);

  const toggleBlur = () => {
    setIsBlurEnabled(!isBlurEnabled);
  };

  const getThemeStyle = () => {
    switch (theme) {
      case 'grayscale':
        return { filter: 'grayscale(100%)' };
      case 'sepia':
        return { filter: 'sepia(100%)' };
      case 'invert':
        return { filter: 'invert(100%)' };
      default:
        return {};
    }
  };

  const createPeer = (initiator = true) => {
    const peer = new Peer({ initiator, stream });

    peer.on('signal', (data) => {
      socket.emit('signal', { roomId, data });
    });

    peer.on('stream', (stream) => {
      if (peerVideoRef.current) {
        peerVideoRef.current.srcObject = stream;
      }
    });

    peerRef.current = peer;
  };

  const sendMessage = () => {
    if (newMessage) {
      const messageData = { roomId, message: newMessage, sender: socket.id };
      socket.emit('send-message', messageData);
      setMessages((prev) => [...prev, messageData]);
      setNewMessage('');
    }
  };

  const endCall = () => {
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
    }
    if (peerRef.current) {
      peerRef.current.destroy();
    }
    navigate('/'); // Redirect to home page after ending the call
  };

  const toggleMute = () => {
    if (stream) {
      const audioTrack = stream.getAudioTracks()[0];
      audioTrack.enabled = !audioTrack.enabled;
      setIsMuted(!audioTrack.enabled);
    }
  };

  const toggleVideo = () => {
    if (stream) {
      const videoTrack = stream.getVideoTracks()[0];
      videoTrack.enabled = !videoTrack.enabled;
      setIsVideoOn(!videoTrack.enabled);
    }
  };

  const shareScreen = () => {
    navigator.mediaDevices.getDisplayMedia({ video: true }).then((screenStream) => {
      if (peerRef.current) {
        peerRef.current.replaceTrack(
          stream.getVideoTracks()[0],
          screenStream.getVideoTracks()[0],
          stream
        );
        screenStream.getTracks()[0].onended = () => {
          peerRef.current.replaceTrack(
            screenStream.getVideoTracks()[0],
            stream.getVideoTracks()[0],
            stream
          );
        };
      }
    });
  };

  return (
    <Container>
      <Typography variant="h4" align="center" gutterBottom>
        Room: {roomId}
      </Typography>
      <VideoContainer>
        <video
          ref={userVideoRef}
          autoPlay
          muted
          style={{ width: '45%', borderRadius: '10px', ...getThemeStyle() }}
        />
        <video ref={peerVideoRef} autoPlay style={{ width: '45%', borderRadius: '10px' }} />
      </VideoContainer>
      <Box display="flex" justifyContent="center" gap={2} mb={4}>
        <IconButton color={isMuted ? 'secondary' : 'primary'} onClick={toggleMute}>
          {isMuted ? <MicOff /> : <Mic />}
        </IconButton>
        <IconButton color={isVideoOn ? 'primary' : 'secondary'} onClick={toggleVideo}>
          {isVideoOn ? <Videocam /> : <VideocamOff />}
        </IconButton>
        <IconButton color="primary" onClick={shareScreen}>
          <ScreenShare />
        </IconButton>
        <IconButton color={isBlurEnabled ? 'primary' : 'secondary'} onClick={toggleBlur}>
          <BlurOn />
        </IconButton>
        <IconButton
          color={theme === 'grayscale' ? 'primary' : 'secondary'}
          onClick={() => setTheme('grayscale')}
        >
          <FilterNone />
        </IconButton>
        <IconButton
          color={theme === 'invert' ? 'primary' : 'secondary'}
          onClick={() => setTheme('invert')}
        >
          <InvertColors />
        </IconButton>
        <IconButton color="secondary" onClick={endCall}>
          <CallEnd />
        </IconButton>
      </Box>
      <Box display="flex" justifyContent="space-between" mt={3}>
        <Box flex={1} mr={2}>
          <ChatContainer elevation={3}>
            <Typography variant="h6" gutterBottom>
              Chat
            </Typography>
            <MessageList>
              {messages.map((msg, index) => (
                <ListItem key={index}>
                  <ListItemAvatar>
                    <Avatar>{msg.sender ? msg.sender[0] : 'U'}</Avatar>
                  </ListItemAvatar>
                  <ListItemText primary={msg.sender || 'Unknown'} secondary={msg.message} />
                </ListItem>
              ))}
            </MessageList>
            <Box display="flex">
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Type a message"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
              />
              <Button variant="contained" color="primary" onClick={sendMessage} style={{ marginLeft: '10px' }}>
                Send
              </Button>
            </Box>
          </ChatContainer>
        </Box>
      </Box>
      <canvas ref={canvasRef} style={{ display: 'none' }} />
    </Container>
  );
};

export default CallRoom;
